import './App.css';




import React, { useState } from "react";

function App() {
  const [skills, setSkills] = useState("");
  const [interest, setInterest] = useState("tech");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const [jobListings, setJobListings] = useState([]);
  const [jobsLoading, setJobsLoading] = useState(false);
  const [jobsError, setJobsError] = useState(null);

  const fetchJobs = async (keyword) => {
  setJobsLoading(true);
  setJobsError(null);
  try {
    const response = await fetch(`http://127.0.0.1:8000/jobs?what=${encodeURIComponent(keyword)}`);

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    if (data.jobs) {
      setJobListings(data.jobs);
    } else {
      setJobListings([]);
    }
  } catch (error) {
    setJobsError(error.message);
    setJobListings([]);
  }
  setJobsLoading(false);
};


<h1 className="text-3xl font-bold text-blue-600">Hello Tailwind</h1>



  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    setJobListings([]);
    setJobsError(null);

    const inputData = {
      skills: skills.split(",").map((s) => s.trim().toLowerCase()), // array of strings
      interests: [interest.toLowerCase()], // array with one string
    };

    try {
      const response = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(inputData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || "Server error");
      }

      const data = await response.json();
      setResult(data.recommendation);

      // Fetch jobs for the recommended career
    if (data.recommendation && data.recommendation.length > 0 && data.recommendation[0].career) {
      await fetchJobs(data.recommendation[0].career);
      }

    } catch (error) {
      console.error("Prediction failed:", error);
      setResult([{ career: "Error", fit: 0 }]);
    }

    setLoading(false);
  };

  return (
  <div className="container">
    <h1>Skillmap AI — Career Predictor</h1>

    <form onSubmit={handleSubmit}>
      {/* Skills Input */}
      <label>Skills (comma-separated):</label>
      <input
        type="text"
        value={skills}
        onChange={(e) => setSkills(e.target.value)}
        placeholder="e.g. python, programming"
        required
      />

      {/* Interest Select */}
      <label>Interests:</label>
      <select
        value={interest}
        onChange={(e) => setInterest(e.target.value)}
      >
        <option value="tech">Tech</option>
        <option value="business">Business</option>
        <option value="art">Art</option>
        <option value="marketing">Marketing</option>
      </select>

      <button type="submit">
        {loading ? "Loading..." : "Get Career Recommendation"}
      </button>
    </form>

    {result && (
      <>
        <h3>Recommended Careers:</h3>
        <ul>
          {result.map((item, index) => (
            <li key={index}>
              <strong>{item.career}</strong> — Fit Score: {item.fit.toFixed(2)}%
            </li>
          ))}
        </ul>
      </>
    )}

    {jobListings && jobListings.length > 0 && (
      <>
        <h3>Adzuna Job Listings:</h3>
        <ul>
          {jobListings.map((job, index) => (
            <li key={index}>
              <a href={job.redirect_url} target="_blank" rel="noreferrer">
                <strong>{job.title}</strong> — {job.location.display_name}<br />
                Salary: {job.salary_min} - {job.salary_max}
              </a>
            </li>
          ))}
        </ul>
      </>
    )}
  </div>
);


      {/* Job listings section */}
      {jobsLoading && <p>Loading job listings...</p>}

      {jobsError && (
        <p style={{ color: "red" }}>Error loading jobs: {jobsError}</p>
      )}

      {jobListings.length > 0 && (
        <div style={{ marginTop: "2rem" }}>
          <h3>Job Listings for {result[0].career}:</h3>
          <ul>
            {jobListings.map((job, index) => (
              <li key={index} style={{ marginBottom: "1rem" }}>
                <a
                  href={job.redirect_url}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{ fontWeight: "bold", color: "#0077cc" }}
                >
                  {job.title}
                </a>
                <p>{job.location.display_name}</p>
                <p>
                  Salary:{" "}
                  {job.salary_min
                    ? `$${job.salary_min.toLocaleString()}`
                    : "N/A"}{" "}
                  -{" "}
                  {job.salary_max
                    ? `$${job.salary_max.toLocaleString()}`
                    : "N/A"}
                </p>
                <p>{job.description}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
}

export default App;
